+body {
+  font-family: 'Segoe UI', sans-serif;
+  background: #f4f8fb;
+  color: #333;
+  margin: 0;
+  padding: 0;
+}
+
+.game-container {
+  max-width: 95%;
+  margin: 30px auto;
+  padding: 20px;
+  background: white;
+  border-radius: 10px;
+  overflow: auto;
+  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
+}
+
+.game-title {
+  text-align: center;
+  font-size: 2rem;
+  color: #0084ff;
+  margin-bottom: 10px;
+}
+
+.image{
+  display: flex;
+  width: 100%;
+  justify-content: center;
+  align-items: center;
+}
+
+.image img {
+  width: 10=50px;
+  height: 100px;
+}
+
+/* Controls */
+.controls {
+  display: flex;
+  justify-content: space-between;
+  flex-wrap: wrap;
+  gap: 15px;
+  margin-bottom: 20px;
+}
+
+.grid-controls,
+.algo-controls {
+  display: flex;
+  align-items: center;
+  gap: 10px;
+  flex-wrap: wrap;
+}
+
+.grid-controls input {
+  width: 60px;
+  padding: 5px;
+  border-radius: 5px;
+  border: 1px solid #ccc;
+}
+
+button {
+  padding: 6px 14px;
+  background: #0077ff;
+  color: white;
+  border: none;
+  border-radius: 6px;
+  font-weight: bold;
+  cursor: pointer;
+  transition: background 0.3s ease;
+}
+
+button:hover {
+  background: #005fcc;
+}
+
+select {
+  padding: 6px 10px;
+  border-radius: 5px;
+  border: 1px solid #ccc;
+}
+
+/* Grid & Cell Styles */
+.grid {
+  display: grid;
+  margin-top: 20px;
+  gap: 2px;
+  justify-content: center;
+}
+
+.cell {
+  width: 40px;
+  height: 40px;
+  border: 1px solid #aaa;
+  text-align: center;
+  line-height: 40px;
+  font-weight: bold;
+  cursor: pointer;
+  transition: background-color 0.3s ease, transform 0.2s ease;
+}
+
+.open { background-color: white; }
+.block { background-color: white; color: white; }
+.start { background-color: #90ee90; }
+.goal { background-color: #ffe99b; }
+
+#drone-fly {
+  position: absolute;
+  width: 110px;
+  transition: top 2s ease, left 2s ease;
+  z-index: 10;
+}
+
+#grid-container {
+  position: relative;
+}
+
+/* Countdown */
+#countdown-screen {
+  position: fixed;
+  top: 0;
+  left: 0;
+  width: 100vw;
+  height: 100vh;
+  background: rgba(0, 0, 0, 0.85);
+  color: white;
+  display: none;
+  align-items: center;
+  justify-content: center;
+  z-index: 9999;
+}
+
+.count-text {
+  font-size: 10rem;
+  font-weight: bold;
+  animation: scaleIn 0.6s ease-in-out forwards;
+}
+
+.nodeExample1 {
+  padding: 5px 10px;
+  border-radius: 8px;
+  background-color: #f5f5f5;
+  border: 2px solid #0a84ff;
+  font-size: 14px;
+}
+
+@keyframes scaleIn {
+  0% {
+    transform: scale(0);
+    opacity: 0;
+  }
+  50% {
+    transform: scale(1.2);
+    opacity: 1;
+  }
+  100% {
+    transform: scale(1);
+    opacity: 1;
+  }
+}
+
+/* Legend */
+.legend {
+  background: #f9f9f9;
+  border-left: 4px solid #0084ff;
+  padding: 10px 15px;
+  margin: 20px 0;
+  border-radius: 5px;
+}
